import { mapGetters, mapActions } from "vuex";
import swal from "sweetalert";

export default {
  data() {
    return {
      title: localStorage.getItem("blogTitle"),
      description: localStorage.getItem("blogDescription"),
      id: localStorage.getItem("blogId"),
    };
  },
  computed: {
    ...mapGetters(["blogsList"]),
    accept() {
      return (
        this.title && this.description && this.title.split(" ").length < 10
      );
    },
  },
  methods: {
    ...mapActions(["editBlog"]),
    editingBlog() {
      if (this.accept) {
        const editedBlog = {
          id: this.id,
          title: this.title,
          description: this.description,
        };
        this.editBlog(editedBlog);
        swal({
          text: "Blog has been edited successfully",
          icon: "success",
        });
        this.$router.push("/");
      } else if (this.title.split(" ").length >= 10) {
        swal({
          text: "Title must not be greater than 10 words",
          icon: "warning",
        });
      } else {
        swal({
          text: "Fill the details",
          icon: "error",
        });
      }
    },
  },
};
