import { mapActions, mapGetters } from "vuex";
import swal from "sweetalert";
import DeleteModal from "@/components/DeleteModal";
export default {
  props: ["blog"],
  name: "ViewBlogsCard",
  components: {
    DeleteModal,
  },
  data() {
    return {
      toBeDeleted: false,
    };
  },
  directives: {
    rainbow: {
      // eslint-disable-next-line no-unused-vars
      bind(el, binding, vnode) {
        el.style.color = "#" + Math.random().toString(16).slice(2, 8);
      },
    },
  },
  mounted() {
    document.getElementsByClassName("more")[0].style.display = "none";
  },
  computed: {
    ...mapGetters(["blogToEdit"]),
  },
  methods: {
    ...mapActions(["deleteBlog"]),
    deletedBlog(id) {
      this.toBeDeleted = false;
      this.deleteBlog(id);
      swal({
        text: "Blog has been deleted successfully",
        icon: "success",
      });
      location.reload();
    },
    editingBlog(blog) {
      this.$store.state.blog = blog;
      this.$router.push({ name: "EditBlog", params: { id: blog.id } });
    },
    myFunction() {
      var dots = document.getElementsByClassName("dots")[0];
      var moreText = document.getElementsByClassName("more")[0];
      var btnText = document.getElementsByClassName("myBtn")[0];
      if (dots.style.display === "none") {
        dots.style.display = "inline";
        btnText.innerHTML = "Read more";
        moreText.style.display = "none";
      } else {
        dots.style.display = "none";
        btnText.innerHTML = "Read less";
        moreText.style.display = "inline";
      }
    },
    showModal() {
      this.toBeDeleted = true;
    },
    dontShow() {
      this.toBeDeleted = false;
    },
  },
};
